import React from 'react';
import './App.css';
import TopBar from "./webpage/TopBar.js";
import ArticleWrapper from "./components/ArticleWrapper.js"
import { Route, BrowserRouter, Switch } from  'react-router-dom';
import NewsAPI from 'newsapi';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';  
import Button from '@material-ui/core/Button'; 
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

class App extends React.Component {

  state = {
    technologySources: "",
    sportsSources: "",
    entertainmentSources: "",
    query:"",
    filter:"",
    articles: []
  };

  getQ = (event) => {
    this.setState({query: event.target.value});
  }
  getReq = (filter) => {
    let query = this.state.query;
    console.log('running');
    this.getArticles(query, filter);
  }
  setFilter = (event) => {
    this.setState({filter: event.target.value});
  }
  getFilter = () => {
    let filter = this.state.filter;
    this.getReq(filter);
    console.log(filter);
  }

  getSources() {

    const newsapi = new NewsAPI('78b9d599c4f94f8fa3afb1a5458928d6');

    let entertainmentSources = "";
    let technologySources = "";
    let sportsSources = "";

    newsapi.v2.sources({
        category: 'technology',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            technologySources += response.sources[i].id + ",";
        }
        technologySources = technologySources.replace(/,\s*$/, "");
        this.setState({technologySources: technologySources});
    });

    newsapi.v2.sources({
        category: 'entertainment',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            entertainmentSources += response.sources[i].id + ",";
        }
        entertainmentSources = entertainmentSources.replace(/,\s*$/, "");
        this.setState({entertainmentSources: entertainmentSources});
    });

    newsapi.v2.sources({
        category: 'sports',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            sportsSources += response.sources[i].id + ",";
        }
        sportsSources = sportsSources.replace(/,\s*$/, "");
        this.setState({sportsSources: sportsSources});
    });
  }

  getArticles(query, filter) {
    const newsapi = new NewsAPI('78b9d599c4f94f8fa3afb1a5458928d6');
    let source;
    if (filter === "Technology") {
      source = this.state.technologySources;
    } else if (filter === "Sports") {
      source = this.state.sportsSources;
    } else if (filter === "Entertainment") {
      source = this.state.entertainmentSources;
    } else {
      source = this.state.technologySources + ',' + this.state.sportsSources + ',' + this.state.entertainmentSources;
    }

    newsapi.v2.everything({
      q: query,
      sources: source,
      language: 'en'
    }).then(response => {
      console.log(response);
      this.setState({articles: response.articles});
  }, err => {
    console.error(err);
  });
}

getHeadlines() {

  const NewsAPI = require('newsapi');
  const newsapi = new NewsAPI('78b9d599c4f94f8fa3afb1a5458928d6');
  // To query /v2/top-headlines
  // All options passed to topHeadlines are optional, but you need to include at least one of them
  newsapi.v2.topHeadlines({
    q: 'bucanneers',
    category: 'sports',
    language: 'en',
  }).then(response => {
    console.log(response);
    /*
      {
        status: "ok",
        articles: [...]
      }
    */
  });

}

  componentDidMount() {
    this.getSources();

  }

  render() {
    return (
<div>
<FormControl>
        <Select
        defaultValue="All"
        id="filter"
        onChange={this.setFilter}
        >
        <MenuItem value={"All"}>All</MenuItem>
          <MenuItem value={"Technology"}>Technology</MenuItem>
          <MenuItem value={"Sports"}>Sports</MenuItem>
          <MenuItem value={"Entertainment"}>Entertainment</MenuItem>
        </Select>
      </FormControl>



        <TextField placeholder="search..." id="searchInput" value={this.state.query} onChange={this.getQ}>
        </TextField>
        <Button variant="contained" color="primary" size="small" onClick={this.getFilter}>search</Button>


        <hr />


        <ArticleWrapper technologySources={this.state.technologySources} sportsSources={this.state.sportsSources} entertainmentSources={this.state.entertainmentSources} articles={this.state.articles}/>

    </div>
   );
  }
}

export default App;
