import React from 'react';
import './App.css';
import TopBar from "./webpage/TopBar.js";
import { Route, BrowserRouter, Switch } from  'react-router-dom';
import NewsAPI from 'newsapi';


class App extends React.Component {

  state = {
    technologySources: "",
    sportsSources: "",
    entertainmentSources: ""
  };

  getSources() {

    const newsapi = new NewsAPI('ed652c3149854660aa18c4ab292ebc28');

    let entertainmentSources = "";
    let technologySources = "";
    let sportsSources = "";

    newsapi.v2.sources({
        category: 'technology',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            technologySources += response.sources[i].id + ",";
        }
        technologySources = technologySources.replace(/,\s*$/, "");
        this.setState({technologySources: technologySources});
    });

    newsapi.v2.sources({
        category: 'entertainment',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            entertainmentSources += response.sources[i].id + ",";
        }
        entertainmentSources = entertainmentSources.replace(/,\s*$/, "");
        this.setState({entertainmentSources: entertainmentSources});
    });

    newsapi.v2.sources({
        category: 'sports',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            sportsSources += response.sources[i].id + ",";
        }
        sportsSources = sportsSources.replace(/,\s*$/, "");
        this.setState({sportsSources: sportsSources});
    });
  }

  getArticles() {
    newsapi.v2.everything({
      sources: sportsSources,
      langauge: 'en',
      q: 'buccaneers',
      pageSize: '100'
    }).then(res => {
      console.log(res)
    });
  }

  componentDidMount() {
    this.getSources();
  }

  render() {
    return (
      <div className="App">
        <BrowserRouter>

        <Route>
                  <TopBar/>
                </Route>
                </BrowserRouter>

      </div>
    );
  }
}

export default App;
