import React from 'react';
import './App.css';
import TopBar from "./webpage/TopBar.js";
import ArticleWrapper from "./components/ArticleWrapper.js"
import { Route, BrowserRouter, Switch } from  'react-router-dom';
import NewsAPI from 'newsapi';
import Container from '@material-ui/core/Container';

class App extends React.Component {

  state = {
    technologySources: "",
    sportsSources: "",
    entertainmentSources: "",
    query:""
  };

  getQ = (event) => {
    this.setState({query: event.target.value});
  }
  getReq = () => {
    let query = this.state.query;
    console.log(query);
  }

    render() {
        return(
    <div>
    <FormControl >
        <Select
        defaultValue="All"
        >
        <MenuItem value={"All"}>All</MenuItem>
          <MenuItem value={"Technology"}>Technology</MenuItem>
          <MenuItem value={"Sports"}>Sports</MenuItem>
          <MenuItem value={"Entertainment"}>Entertainment</MenuItem>
        </Select>
      </FormControl>



        <TextField placeholder="search..." id="searchInput" value={this.state.query} onChange={this.getQ}>
        </TextField>
        <Button variant="contained" color="primary" size="small" onClick={this.getReq}>search</Button>


        <hr />



    </div>


        )



}

  getSources() {

    const newsapi = new NewsAPI('ed652c3149854660aa18c4ab292ebc28');

    let entertainmentSources = "";
    let technologySources = "";
    let sportsSources = "";

    newsapi.v2.sources({
        category: 'technology',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            technologySources += response.sources[i].id + ",";
        }
        technologySources = technologySources.replace(/,\s*$/, "");
        this.setState({technologySources: technologySources});
    });

    newsapi.v2.sources({
        category: 'entertainment',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            entertainmentSources += response.sources[i].id + ",";
        }
        entertainmentSources = entertainmentSources.replace(/,\s*$/, "");
        this.setState({entertainmentSources: entertainmentSources});
    });

    newsapi.v2.sources({
        category: 'sports',
        language: 'en'
    }).then(response => {
        for (let i = 0; i < response.sources.length; i++) {
            sportsSources += response.sources[i].id + ",";
        }
        sportsSources = sportsSources.replace(/,\s*$/, "");
        this.setState({sportsSources: sportsSources});
    });
  }

  componentDidMount() {
    this.getSources();
  }

  render() {
    return (
<div>
<FormControl >
        <Select
        defaultValue="All"
        >
        <MenuItem value={"All"}>All</MenuItem>
          <MenuItem value={"Technology"}>Technology</MenuItem>
          <MenuItem value={"Sports"}>Sports</MenuItem>
          <MenuItem value={"Entertainment"}>Entertainment</MenuItem>
        </Select>
      </FormControl>



        <TextField placeholder="search..." id="searchInput" value={this.state.query} onChange={this.getQ}>
        </TextField>
        <Button variant="contained" color="primary" size="small" onClick={this.getReq}>search</Button>


        <hr />

      <Container>
        <TopBar/>
        <ArticleWrapper technologySources={this.state.technologySources} sportsSources={this.state.sportsSources} entertainmentSources={this.state.entertainmentSources}/>
      </Container>
    </div>
   );
  }
}

export default App;
